"use strict";

module.exports = {
  languages: [
    {
      name: "bar",
      parsers: ["bar"],
    },
  ],
  parsers: {
    bar: {
      parse: text => ({ text }),
      astFormat: "bar",
    },
  },
  printers: {
    bar: {
      async print(path) {
        const { default: prettier } = await import(
          "../../../../../config/prettier-entry.js"
        );
        const { concat } = prettier.doc.builders;
        return concat(["content from `prettier-plugin-bar` package + ", path.getValue().text]);
      },
    },
  },
  defaultOptions: {
    tabWidth: 4,
  },
};
